import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  count: 'A'
};

export const Console = createSlice({
  name: 'console', 
  initialState, 
  reducers: {
    ConsoleA: (state) => {
      state++;
    }, 
    ConsoleB: (state) => {
      state--;
    }
  }
});

export const {ConsoleA, ConsoleB} = Console.actions;
export default Console.reducer;
