import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    pizzas: {

    }
};

export const counterSlice = createSlice({
    name: 'changeCount', 
    initialState, 
    reducers:{
        addPizza : (title, price, imageUrl, sizes, types) => {
            initialState.pizzas.push([title, price, imageUrl, sizes, types]);
        }, 
    }
});

export const {addPizza} = counterSlice.actions;
export default counterSlice.reducer;
