import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  categoryId: 0,
  sort: { name: "популярности", sortProperty: "rating" },
};

const filterSlice = createSlice({
  name: "filters",
  initialState,
  reducers: {
    setCategoryId(state, action) {
      state.categoryId = action.payload;
    },
    setSort(state, action) {
      state.sort = action.payload;
    }
  },
});

const addPizzas = createSlice({
  name: 'addPizzas', 
  initialState: {
    pizzas: 0
  }, 
  reducers: {
    incrementPizzas: (state) => {
      state.value += 1
    }, 
    decrementPizzas : (state) => {
      state.value -= 1
    }
  }
})

const actions = [
  filterSlice.actions.setCategoryId, 
  filterSlice.actions.setSort, 
  addPizzas.actions.incrementPizzas, 
  addPizzas.actions.decrementPizzas,
]
const reducers = [
  filterSlice.reducer, 
  addPizzas.reducer
]
export default {actions, reducers}
