import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  count: 'A'
};

export const console = createSlice({
  name: 'console', 
  initialState, 
  reducers: {
    ConsoleA: (state) => {
      console.log(state, 'A')
    }, 
    ConsoleB: (state, action) => {
      console.log(state, 'B')
    }
  }
});

export const {ConsoleA, removePizzas} = addPizzas.actions;
export default addPizzas.reducer;
