import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  count: 0
};

export const addPizzas = createSlice({
  name: 'addPizzas', 
  initialState, 
  reducers: {
    addPizza: (state, action) => {
      state++;
    }
  }
})
