import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  count: 0
};

export const addPizzas = createSlice({
  name: 'addPizzas', 
  initialState, 
  reducers: {
    addPizza: (state, action) => {
      initialState = action.getState(state++)
    }, 
    removePizza: (state, action) => {
      initialState = action.getState(state--)
    }
  }
});

export const {addPizza, removePizzas} = addPizzas.actions;
export default addPizzas.reducer
