import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  count: 0
};

export const addPizzas = createSlice({
  name: 'addPizzas', 
  initialState, 
  reducers: {
    addPizza: (state) => {
      state++;
    }, 
    removePizzas: (state) => {
      state--;
    }
  }
});

export const {addPizza, removePizzas} = addPizzas.actions;
